	/**
	 * Create a segmented profile from pig data. Based on the nuclei in
	 * \Testing_Christchurch_pig\Pig_Subset_Fertile\
	 * @return
	 * @throws Exception
	 */
	public static SegmentedProfile pigSpermSegmentedMedianProfile() throws Exception {
		
		// Segment boundaries are chosen from prior segmentation
		NucleusBorderSegment seg0 = new NucleusBorderSegment(0, 16, 327);
		NucleusBorderSegment seg1 = new NucleusBorderSegment(16, 147, 327);
		NucleusBorderSegment seg2 = new NucleusBorderSegment(147, 163, 327);
		NucleusBorderSegment seg3 = new NucleusBorderSegment(163, 182, 327);
		NucleusBorderSegment seg4 = new NucleusBorderSegment(182, 312, 327);
		NucleusBorderSegment seg5 = new NucleusBorderSegment(312, 326, 327);
		
		List<NucleusBorderSegment> list = new ArrayList<NucleusBorderSegment>();
		list.add(seg0);
		list.add(seg1);
		list.add(seg2);
		list.add(seg3);
		list.add(seg4);
		list.add(seg5);
		
		NucleusBorderSegment.linkSegments(list);
		
		// Add the segments to the sample data
		SegmentedProfile p = new SegmentedProfile(pigSpermMedianProfile(), list);
		return p;

		
	}

